                                    <table class="sub-table">
                                        <tr><td class="lbl">Working Days</td><td style="text-align:center;">${D.total}</td></tr>
                                        <tr><td class="lbl">Present</td><td style="text-align:center;">${D.present}</td></tr>
                                        <tr><td class="lbl">Absent</td><td style="text-align:center;">${D.absent}</td></tr>
                                    </table>
                                    
